import React, { useState } from "react";
import { Mic, Send } from "lucide-react";

const MODES = {
  ANALYZE: "Analyze Mood",
  CRISIS: "Detect Crisis",
  SUMMARIZE: "Summarize",
};

export default function App() {
  const [input, setInput] = useState("");
  const [selected, setSelected] = useState(MODES.ANALYZE);
  const [output, setOutput] = useState("");
  const [listening, setListening] = useState(false);

  const handleSend = () => {
    if (!input.trim()) return;
    setOutput(`Response from /${selected}:\n(simulated output here)`);
    setInput("");
  };

  const handleMic = () => {
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Speech recognition not supported in this browser.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;

    recognition.onstart = () => setListening(true);
    recognition.onerror = () => setListening(false);
    recognition.onend = () => setListening(false);

    recognition.onresult = (e) => {
      const transcript = e.results[0][0].transcript;
      setInput((prev) => prev + " " + transcript);
    };

    recognition.start();
  };

  return (
    <div className="min-h-screen w-full bg-[#343541] text-white flex flex-col items-center px-4 sm:px-8 py-6 font-sans">
      <h1 className="text-2xl font-semibold mb-6">MoodBot</h1>

      <div className="w-full max-w-4xl">
        {/* Tabs */}
        <div className="flex flex-wrap gap-2 mb-6">
          {Object.entries(MODES).map(([key, label]) => (
            <button
              key={key}
              onClick={() => {
                setSelected(label);
                setInput("");
                setOutput("");
              }}
              className={`px-4 py-1 rounded-full text-sm border ${
                selected === label
                  ? "bg-white text-black"
                  : "border-zinc-500 text-zinc-300 hover:bg-zinc-700"
              } transition`}
            >
              {label}
            </button>
          ))}
        </div>

        {/* Output */}
        <div className="min-h-[250px] bg-[#40414f] rounded-md p-4 mb-6 text-sm whitespace-pre-line">
          {output || (
            <span className="text-zinc-400">Your response will appear here...</span>
          )}
        </div>

        {/* Input */}
        <div className="flex items-center bg-[#40414f] border border-zinc-600 rounded-md px-4 py-2">
          <input
            type="text"
            className="flex-1 bg-transparent outline-none text-sm placeholder-zinc-400"
            placeholder={`Enter text for "${selected}"`}
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
          />
          <Mic
            className={`w-5 h-5 cursor-pointer ${
              listening ? "text-green-500 animate-pulse" : "text-zinc-400"
            } mr-2`}
            onClick={handleMic}
          />
          <button
            onClick={handleSend}
            className="p-1 bg-white text-black rounded-md hover:scale-105 transition"
          >
            <Send className="w-4 h-4" />
          </button>
        </div>
      </div>
    </div>
  );
}
