import React, { useState, useRef, useEffect } from "react";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [started, setStarted] = useState(false);
  const bottomRef = useRef(null);

  const sendMessage = () => {
    const text = input.trim();
    if (!text) return;

    if (!started) setStarted(true);

    setMessages([...messages, { role: "user", text }]);
    setInput("");
  };

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="min-h-screen bg-zinc-950 text-white flex flex-col items-center">
      <header className="w-full border-b border-zinc-800 text-center py-4 text-lg font-medium text-zinc-300">
        MoodBot
      </header>

      <main className="flex-1 w-full max-w-[700px] px-4 py-6 overflow-y-auto">
        {!started ? (
          <div className="h-full flex flex-col justify-center items-center text-zinc-400">
            <p className="text-2xl mb-2">Ready when you are ðŸ‘‹</p>
            <p className="text-sm text-zinc-600">Type something to begin...</p>
          </div>
        ) : (
          messages.map((msg, i) => (
            <div
              key={i}
              className={`my-2 px-4 py-2 rounded-md w-fit max-w-[90%] break-words text-sm ${
                msg.role === "user"
                  ? "bg-blue-600 ml-auto text-white"
                  : "bg-zinc-800 mr-auto text-white"
              }`}
            >
              {msg.text}
            </div>
          ))
        )}
        <div ref={bottomRef} />
      </main>

      <footer className="w-full max-w-[700px] px-4 py-3 border-t border-zinc-800">
        <div className="flex items-center gap-2">
          <input
            type="text"
            className="flex-1 bg-zinc-800 border border-zinc-700 rounded-md px-4 py-2 text-sm text-white placeholder:text-zinc-500 focus:outline-none focus:ring-1 focus:ring-blue-600"
            placeholder="Message MoodBot..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button
            onClick={sendMessage}
            className="bg-blue-600 text-sm px-4 py-2 rounded-md hover:bg-blue-700 transition"
          >
            Send
          </button>
        </div>
      </footer>
    </div>
  );
}

export default App;
