import React, { useState } from "react";
import { Mic, Send } from "lucide-react";

const MODES = {
  ANALYZE: "analyze mood",
  CRISIS: "detect crisis",
  SUMMARIZE: "summarize",
};

export default function App() {
  const [input, setInput] = useState("");
  const [selected, setSelected] = useState(MODES.ANALYZE);
  const [output, setOutput] = useState("");

  const handleSend = () => {
    if (!input.trim()) return;
    // simulate backend response
    setOutput(`Response from /${selected}: (simulate here)`);
    setInput("");
  };

  return (
    <div className="min-h-screen bg-[#343541] text-white flex flex-col items-center px-4 py-6 font-sans">
      <h1 className="text-2xl font-semibold mb-6">MoodDecode</h1>

      {/* Tabs */}
      <div className="flex gap-2 mb-4">
        {Object.entries(MODES).map(([key, label]) => (
          <button
            key={key}
            onClick={() => {
              setSelected(label);
              setInput("");
              setOutput("");
            }}
            className={`px-4 py-1 rounded-full text-sm border ${
              selected === label
                ? "bg-white text-black"
                : "border-zinc-500 text-zinc-300 hover:bg-zinc-700"
            } transition`}
          >
            {label}
          </button>
        ))}
      </div>

      {/* Output Box */}
      <div className="w-full max-w-2xl min-h-[200px] bg-[#40414f] rounded-md p-4 mb-6 text-sm overflow-auto">
        {output || (
          <span className="text-zinc-400">
            Your response will appear here...
          </span>
        )}
      </div>

      {/* Input Box */}
      <div className="w-full max-w-2xl flex items-center bg-[#40414f] border border-zinc-600 rounded-md px-4 py-2">
        <input
          type="text"
          className="flex-1 bg-transparent outline-none text-sm placeholder-zinc-400"
          placeholder={`Enter text for "${selected}"`}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
        />
        <Mic className="w-5 h-5 text-zinc-400 mr-2 cursor-pointer" />
        <button
          onClick={handleSend}
          className="p-1 bg-white text-black rounded-md hover:scale-105 transition"
        >
          <Send className="w-4 h-4" />
        </button>
      </div>
    </div>
  );
}
