import React, { useState } from "react";

function App() {
  const [active, setActive] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendMessage = () => {
    if (!input.trim()) return;
    setMessages([...messages, { role: "user", text: input }]);
    setInput("");
  };

  if (!active) {
    return (
      <div className="min-h-screen bg-zinc-900 flex items-center justify-center">
        <div
          onClick={() => setActive(true)}
          className="cursor-pointer transition-all duration-500 bg-zinc-800 text-white rounded-2xl px-6 py-4 shadow-lg text-lg hover:scale-105"
        >
          Start chatting...
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-zinc-900 text-white flex flex-col">
      <header className="text-center text-2xl py-4 border-b border-zinc-700">MoodBot</header>
      <main className="flex-1 overflow-y-auto p-4 space-y-3">
        {messages.map((msg, i) => (
          <div key={i} className={`p-3 rounded-lg max-w-xl ${msg.role === "user" ? "bg-zinc-700 self-end" : "bg-zinc-800 self-start"}`}>
            {msg.text}
          </div>
        ))}
      </main>
      <footer className="border-t border-zinc-700 p-4">
        <div className="flex gap-2">
          <input
            type="text"
            className="flex-1 bg-zinc-800 border border-zinc-600 rounded-lg p-2 text-white"
            placeholder="Send a message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button onClick={sendMessage} className="bg-blue-600 px-4 py-2 rounded-lg hover:bg-blue-700">Send</button>
        </div>
      </footer>
    </div>
  );
}

export default App;
