import React, { useState, useRef, useEffect } from "react";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [started, setStarted] = useState(false);
  const bottomRef = useRef(null);

  const sendMessage = () => {
    const text = input.trim();
    if (!text) return;

    if (!started) setStarted(true);

    setMessages([...messages, { role: "user", text }]);
    setInput("");
  };

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  return (
    <div className="min-h-screen bg-zinc-900 text-white flex flex-col">
      <header className="text-center text-xl sm:text-2xl py-4 border-b border-zinc-800 font-semibold">
        MoodBot
      </header>

      <main className={`flex-1 overflow-y-auto px-4 py-6 space-y-4 transition-all duration-500 ${started ? "bg-zinc-900" : "flex items-center justify-center bg-zinc-900"}`}>
        {!started ? (
          <div className="text-center text-zinc-400 text-lg">
            <p className="mb-4 text-xl sm:text-2xl">Ready when you are ðŸ‘‹</p>
            <p className="text-sm text-zinc-500">Type your first message below</p>
          </div>
        ) : (
          messages.map((msg, i) => (
            <div
              key={i}
              className={`p-3 rounded-xl max-w-lg break-words ${
                msg.role === "user"
                  ? "ml-auto bg-blue-600"
                  : "mr-auto bg-zinc-800"
              }`}
            >
              {msg.text}
            </div>
          ))
        )}
        <div ref={bottomRef} />
      </main>

      <footer className="p-4 border-t border-zinc-800 bg-zinc-900">
        <div className="max-w-3xl mx-auto flex gap-2">
          <input
            type="text"
            className="flex-1 bg-zinc-800 border border-zinc-700 rounded-full px-4 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-600"
            placeholder="Send a message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          />
          <button
            onClick={sendMessage}
            className="bg-blue-600 px-5 py-2 rounded-full hover:bg-blue-700 transition"
          >
            Send
          </button>
        </div>
      </footer>
    </div>
  );
}

export default App;
